import React, { Component } from "react";
import styled from "styled-components";

const Wrapper = styled.div``;
const Container = styled.div`
  padding: 0.5em 1em 0.5em 1em;
  display: grid;
  grid-row-gap: 1em;
`;
const OptionsContainer = styled.div`
  padding-left: 5em;
`;
const Question = styled.div`
  display: flex;
  > div {
    line-height: 20px;
  }
  > b {
    padding-right: 1em;
  }
`;
const Option1 = styled.div`
  display: flex;
  cursor: pointer;
  > div {
    line-height: 20px;
  }
  > b {
    padding-right: 1em;
  }
`;
const Option2 = styled.div`
  display: flex;
  cursor: pointer;
  > div {
    line-height: 20px;
  }
  > b {
    padding-right: 1em;
  }
`;
const Option3 = styled.div`
  display: flex;
  cursor: pointer;
  > div {
    line-height: 20px;
  }
  > b {
    padding-right: 1em;
  }
`;
const Option4 = styled.div`
  display: flex;
  cursor: pointer;
  > div {
    line-height: 20px;
  }
  > b {
    padding-right: 1em;
  }
`;
const Count = styled.div`
  /* padding:1em; */
  font-size: 2em;
  font-weight: 600;
  padding-right: 0.5em;
`;
const Explaination = styled.div``;

class Card extends Component {
  state = {
    selected: ""
  };

  clickHandler = e => {
    const { answer } = this.props;
    console.log("in click", e.target.getAttribute("name"));
    if(e.target.getAttribute("name") == answer){
      this.setState({selected:"correct"})
    }
    this.setState({selected:"w"})
  };

  render() {
    // console.log("props", this.props);
    const {
      question,
      option1,
      option2,
      option3,
      option4,
      count,
      answer
    } = this.props;
    return (
      <Wrapper>
        <Container>
          <Question>
            <Count>{count}</Count>
            <div>{question}</div>
          </Question>
          <OptionsContainer>
            <Option1 onClick={e => this.clickHandler(e)} name="option1">
              <b>A</b>
              <div name="option1">{option1}</div>
            </Option1>
            <Option2 onClick={e => this.clickHandler(e)} name="option2">
              <b>B</b>
              <div name="option2">{option2}</div>
            </Option2>
            <Option3 onClick={e => this.clickHandler(e)} name="option3">
              <b>C</b>
              <div name="option3">{option3}</div>
            </Option3>
            <Option4 onClick={e => this.clickHandler(e)} name="option4">
              <b>D</b>
              <div name="option4">{option4}</div>
            </Option4>
          </OptionsContainer>
          <Explaination></Explaination>
        </Container>
      </Wrapper>
    );
  }
}

export default Card;
