import React, { Component } from "react";
import styled from "styled-components";

const Wrapper = styled.div`
  margin: 0 3em 0 3em;
  @media only screen and (max-width: 600px) {
    margin: unset;
  }
`;
const Container = styled.div`
  padding: 0.5em 1em 0.5em 1em;
  display: grid;
  grid-row-gap: 1em;
`;
const OptionsContainer = styled.div`
  padding-left: 5em;
  display: grid;
  grid-row-gap: 0.2em;
  @media only screen and (max-width: 600px) {
    padding-left: unset;
  }
`;
const Question = styled.div`
  display: grid;
  grid-row-gap: 1em;

  > div {
    line-height: 20px;
    /* font-size: 1em; */
    /* padding: 1em 0 1em 0; */
  }
  > b {
    padding-right: 1em;
  }
`;

const Option = styled.div`
  display: grid;
  cursor: pointer;
  grid-template-columns: 0.2fr 8fr;
  grid-gap: 1em;
  background: #d9ffb3;
  > div {
    line-height: 20px;
  }
  > span {
    padding-right: 1em;
    background: #336600;
    color: #fff;
    font-size: 2em;
    border-radius: 50%;
    justify-content: center;
  }
`;

const Count = styled.div`
  /* padding:1em; */
  font-size: 1.3em;
  font-weight: 600;
  padding-right: 0.5em;
  border-bottom: 1px solid #e6e6e6;
  padding-bottom: 1em;
`;
const Explaination = styled.div``;
const Year = styled.div``;

class Card extends Component {
  state = {
    selected: "",
    show: false,
    right: "#336600",
    wrong: "tomato"
  };

  clickHandler = e => {
    const { answer } = this.props;
    // console.log("in click", e.target.getAttribute("name"), "answer ", answer);
    if (e.target.getAttribute("name") == answer) {
      this.setState({ selected: "correct", show: true });
    } else {
      this.setState({ selected: "wrong", show: true });
    }
  };

  render() {
    // console.log("props", this.props);
    const {
      question,
      option1,
      option2,
      option3,
      option4,
      count,
      answer,
      year
    } = this.props;
    return (
      <Wrapper>
        <Container>
          <Question>
            <Count>Question {count + 1}</Count>
            <div>{question}</div>
          </Question>
          <OptionsContainer>
            <Option onClick={e => this.clickHandler(e)} name="option1">
              <span sty>
                <b>A</b>
              </span>
              <div name="option1">{option1}</div>
            </Option>
            <Option onClick={e => this.clickHandler(e)} name="option2">
              <span>
                <b>B</b>
              </span>
              <div name="option2">{option2}</div>
            </Option>
            <Option onClick={e => this.clickHandler(e)} name="option3">
              <span>
                <b>C</b>
              </span>
              <div name="option3">{option3}</div>
            </Option>
            <Option onClick={e => this.clickHandler(e)} name="option4">
              <span>
                <b>D</b>
              </span>
              <div name="option4">{option4}</div>
            </Option>
          </OptionsContainer>
          <Explaination style={{ display: this.state.show }}>
            {this.state.selected}
            answer : {answer}
            <Year>UPSC {year}</Year>
          </Explaination>
        </Container>
      </Wrapper>
    );
  }
}

export default Card;
