import React, { Component } from "react";
import styled from "styled-components";
import { connect } from "react-redux";
import { getupscSubjectQuery } from "../actions/asyncActions";
import Card from "../hoc/card";
const Wrapper = styled.div``;
const Container = styled.div`
  display: grid;
  grid-column-gap: 2em;
  margin: 0 3em 0 3em;
  > h1 {
    text-align: center;
  }
`;
class Questions extends Component {
  state = {
    query: "",
    correct: 0
  };

  componentDidMount = () => {
    // this.props.subjectQuery("History");
    const url = window.location.href.split("=")[1];
    this.setState({ query: url });
    if (url.includes("_")) {
      // console.log("url", url.replace("_", " "));
      // this.props.subjectQuery(url.replace("_", " "));
      getupscSubjectQuery(url);
    } else {
      getupscSubjectQuery(url);
    }
  };

  totalCorrect = data => {

    this.setState({ correct: this.state.correct + 1 });
  };

  render() {
    const { upscQuestion } = this.props;
    // console.log("upscQuestion props", upscQuestion);
    const Question = upscQuestion
      ? upscQuestion.map((d, i) => {
          return <Card {...d} count={i} totalCorrect={this.totalCorrect} />;
        })
      : [];

    return (
      <Wrapper>
        <Container>
          <h1>{this.state.query}</h1>
          {Question}
        </Container>
      </Wrapper>
    );
  }
}

const mapStateToProps = store => {
  // console.log("store", store);
  return {
    upscQuestion: store.upscSubjectQuestions
  };
};
const mapDispatchToProps = dispatch => {
  return {
    subjectQuery: data => dispatch(getupscSubjectQuery(data))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Questions);
