  NProgress.start();
};
Router.onRouteChangeComplete = () => {
  console.log(url);
  NProgress.done();
};
Router.onRouteChangeError = () => NProgress.done();

export default ({ children, title }) => (
  <div class="root">
    <Head>
      <title>UPSC Pyq</title>
      <link
        rel="stylesheet"
        href="https://cdnjs.cloudflare.com/ajax/libs/nprogress/0.2.0/nprogress.css"
      ></link>
    </Head>
   <Header/>
    <h1>{title}</h1>
    {children}
    <footer>&copy; {new Date().getFullYear()}</footer>
    <style jsx>
      {`
        .root {
          width: 100%;
          display: flex;
          justify-content: center;
          align-items: center;
          flex-direction: column;
        }
        header {
          width: 100%;
          display: flex;
          justify-content: space-around;
          padding: 1em;
          font-size: 1.2rem;
          background: indigo;
        }
        header a {
          color: darkgrey;
          text-decoration: none;
        }
        header a:hover {
          font-weight: bold;
          color: lightgrey;
        }
        footer {
          padding: 1em;
        }
      `}
    </style>
  </div>
);
